from flask import Flask, requestimport jsonimport pickleimport numpy as npapp = Flask(__name__)def load_models():        file_name = "models/best_rf.p"    with open(file_name, 'rb') as f:        data = pickle.load(f)    return data@app.route('/predict', methods=['GET'])def predict():        request_json = request.get_json()    X = request_json['input'] # the input is a list, of feature values    X = np.array(X).reshape(1,-1)        # load model    model = load_models()    prediction = model.predict(X)[0]    response = json.dumps({'response': prediction})    return response, 200if __name__ == '__main__':    app.run(debug=True)    